module.exports = (node, graph) => {
  let ws;

  node.onReady = () => {
    ws = new WebSocket("ws://localhost:8888");
    ws.onopen = () => {
      console.log('New client connection opened! Saying hello to server.');
      ws.send(JSON.stringify({ 
        type: 'message', 
        args: 'Hello server! Im new client', 
        meta: {}
      }));
    };
    ws.onmessage = async (msg) => {
      const { type, args, meta } = JSON.parse(msg.data); 
      console.log(`Server ${type} received:`, args, meta); 
    }
  };

  node.onDestroy = () => {
    console.log('Node destroyed');  
    ws.close();
    console.log('Socket closed');    
  };
};
